package perminstr;

import org.junit.Assert;
import org.junit.BeforeClass;
import org.junit.Test;


public class PermInStrTest {


    private static PermInStr pis = new PermInStr();

    @BeforeClass
    public static void setUp() throws Exception {
        pis = new PermInStr();
    }

    @Test
    public void case0() {
        Assert.assertTrue(pis.checkInclusion("ab", "eidbaooo"));
    }

    @Test
    public void case1() {
        Assert.assertFalse(pis.checkInclusion("ab", "eidboaoo"));
    }

    @Test
    public void case2() {
        Assert.assertTrue(pis.checkInclusion("ab","ab"));
    }

    @Test
    public void case3() {
        Assert.assertTrue(pis.checkInclusion("ba","ab"));
    }

    @Test
    public void case4() {
        Assert.assertTrue(pis.checkInclusion("a","a"));
    }

    @Test
    public void case5() {
        Assert.assertFalse(pis.checkInclusion("a","b"));
    }

    @Test
    public void case6() {
        Assert.assertFalse(pis.checkInclusion("a",""));
    }

    @Test
    public void case7() {
        Assert.assertTrue(pis.checkInclusion("adc","dcda"));
    }

    @Test
    public void case8() {
        String s1 = "";
        String s2 = "";
        Assert.assertTrue(pis.checkInclusion(s1,s2));
    }
}